import "./null.scss";
import "./ui.scss";
import "./App.scss";
import "./scheme.scss";
import React from "react";
import { Routes, Route } from "react-router-dom";
import Layout from "./components/Layout.jsx";
import menuItems from "./data/header-menu.json";

import Home from "./pages/Home";

import NotFound from "./pages/NotFound/index.jsx";

function App() {
  const [projects, setProjects] = React.useState([]);
  const [modal, setModal] = React.useState({ active: false, index: 0 });

  React.useEffect(() => {
    setMenuNav(menuItems);
    setMenuIcons(servicesIcons);
    setProjects(projectsPortfolio.slice().reverse().slice(0, 4));

    document.addEventListener("scroll", () => {
      if (window.scrollY > 100) {
        setScrollReady(true);

        if (window.scrollY > 300) {
          setBurgerFixed(true);
        } else {
          setBurgerFixed(false);
        }
      } else {
        setScrollReady(false);
      }
    });

    loadData();
  }, []);

  return (
    <Routes>
      <Route path="/" element={<Layout />}>
        <Route
          index
          path="/"
          element={
            <Home
              projects={projects}
              modal={modal}
              setModal={setModal}
              setScrollTargetMarker={setScrollTargetMarker}
              scrollReady={scrollReady}
            />
          }
        />
        <Route path="/about" element={<div>About me</div>} />

        <Route path="*" element={<NotFound />} />
      </Route>
    </Routes>
  );
}

export default App;
