import Logo from "./components/logo";

import linkedIn from "./img/icons/linkedin-icon.svg"
import drrrible from "./img/icons/dribbble-icon.svg"
import instagram from "./img/icons/instagram-icon.svg"
import github from "./img/icons/github-icon.svg"


import Hounter from './img/preview/hounter.png'
import CafeStreet from './img/preview/CafeStreet.png'
import ArtNLife from './img/preview/art&life.png'
import SashaShavit from './img/preview/SashaShavit.png'
import Memcoin from './img/preview/memcoin.png'
import ToDo from './img/preview/todo.png'


import  './null.scss'
import  './ui.scss'
import './App.scss';
import './scheme.scss'
import React from "react"

import useDarkScheme from './hooks/useDarkScheme.jsx';
import SmoothScroll from "./components/smoothScroll";
import GsapMagnetic from "./components/GsapMagnetic"

import AOS from 'aos';
import 'aos/dist/aos.css';

import menuItems from "./data/header-menu.json";
import MenuEl from "./components/headerMenuEl";
import Button from "./components/button";
import ThemeButton from "./components/theme-button";
import FooterBlock from "./components/footer-block";

import Home from "./pages/Home";
import Preloader from "./components/preloader";

import {Routes, Route} from 'react-router-dom';

const projectsPortfolio = [
  {
      "title": "Hounter",
      "label": "development",
      "imageUrl": Hounter
  },
  {
      "title": "CafeStreet",
      "label": "development",
      "imageUrl": CafeStreet
  },
  {
      "title": "Art & Life",
      "label": "development",
      "imageUrl": ArtNLife
  },
  {
      "title": "Sasha Shavit",
      "label": "development",
      "imageUrl": SashaShavit
  },
  {
      "title": "Memcoin",
      "label": "development",
      "imageUrl": Memcoin
  },
  {
      "title": "ToDo App",
      "label": "design & development",
      "imageUrl": ToDo
  }
]

const servicesIcons = [
  {'link': linkedIn, 'alt': 'LinkedIn link', 'url': 'https://www.linkedin.com/in/dima-dzyubenko-a7285420b/'},
  {'link': drrrible, 'alt': 'Drrrible link', 'url': 'https://dribbble.com/benko_web_dev'},
  {'link': instagram, 'alt': 'Instagram link', 'url': 'https://instagram.com/benko_web_dev?igshid=OGQ5ZDc2ODk2ZA=='},
  {'link': github, 'alt': 'GitHub link', 'url': 'https://github.com/DimaD10'}
]


function App() {
  const { darkScheme, changeScheme } = useDarkScheme();

  const [menuNav, setMenuNav] = React.useState([]);
  const [menuIcons, setMenuIcons] = React.useState([]);
  const [projects, setProjects] = React.useState([]);
  const [scrollReady, setScrollReady] = React.useState(false);
  const [burgerFixed, setBurgerFixed] = React.useState(false)
  const [burgerActive, setBurgerActive] = React.useState(false);
  const [modal, setModal] = React.useState({active: false, index: 0});
  const [overlayActive, setOverlayActive] = React.useState(true)

  const [preloader, setPreloader] = React.useState(true);
  const [preloaderFinish, setPreloaderFinish] = React.useState(false)

  const [scrollTargetMarker, setScrollTargetMarker] = React.useState(false)

  const wrapper = React.useRef()


  const openBurger = () => {
    setBurgerActive(!burgerActive);
    setOverlayActive(!overlayActive)

    if (burgerActive) {
      document.body.style.overflow = "visible";
    } else {
      document.body.style.overflow = "hidden";
    }
  }

  const loadData = async () => {
    try {
      await new Promise((resolve) => setTimeout(resolve, 5000));
      setPreloader(false);
      setPreloaderFinish(true);
      AOS.init();
      document.body.style.overflow = "visible";
      setOverlayActive(!overlayActive)
    } catch (error) {
      console.error('Error', error);
    }
  };

  React.useEffect(() => {
    setMenuNav(menuItems);
    setMenuIcons(servicesIcons);
    setProjects(projectsPortfolio.slice().reverse().slice(0, 4));

    document.addEventListener("scroll", () => {
      if (window.scrollY > 100) {
        setScrollReady(true);

        if (window.scrollY > 300) {
          setBurgerFixed(true);
        } else {
          setBurgerFixed(false);
        }
      } else {
        setScrollReady(false);
      }
    });

    loadData();
  }, [loadData]);

  return (
    <SmoothScroll marker={scrollTargetMarker} markerFunc={setScrollTargetMarker} preloaderFinish={preloaderFinish} setPreloaderFinish={setPreloaderFinish}>
      <div className={`wrapper ${darkScheme ? 'dark' : ''}`} ref={wrapper} data-lenis-prevent={overlayActive ? "true" : undefined}>
        <header className="header">
          <div className="header__wrapper">
            <div className="header__container-big">
              <Logo />
              
              <div className="header__navigation">
                {menuNav.map((item, i) => {
                  return (
                    <MenuEl key={i} href={item.url} text={item.text} current={item.current === "true" ? true : false} />
                  )
                })}
              </div>

              <div className="header__actions">
                <ThemeButton  click={changeScheme} />

                <Button text="Write me" href='' />
              </div>

              <div className={`burger-btn-parrent ${scrollReady ? 'fixed' : ''} ${burgerFixed ? 'showed' : ''} ${burgerActive ? 'burger-active' : ''}` }>
                <div className="burger-btn-parrent__container-big">
                  <GsapMagnetic>
                    <button className={`burger-btn ${burgerActive ? 'active' : ''}`} onClick={() => openBurger()}>
                        <span></span>
                        <span></span>
                    </button>
                  </GsapMagnetic>
                </div>
              </div>

              <div className={`burger-frame ${burgerActive ? 'active' : ''}`}>
                <div className="burger-frame__wrapper">
                  <div className="burger-frame__header burger-frame-anim">
                    <h3 className="burger-frame__header-title">navigation</h3>
                  </div>
                  <div className="burger-frame__menu burger-frame-anim">
                    {menuNav.map((item, i) => {
                      return (
                        <MenuEl key={i} href={item.url} text={item.text} current={item.current === "true" ? true : false} />
                      )
                    })}
                  </div>

                  <div className="burger-frame__header burger-frame-anim">
                    <h3 className="burger-frame__header-title">actions</h3>
                  </div>
                  <div className="burger-frame__actions burger-frame-anim">
                    <Button text="Write me" href='' />

                    <ThemeButton  click={changeScheme} />
                  </div>
                </div>

                <div className="burger-frame__decor"></div>
              </div>
            </div>
          </div>
        </header>
        <main>
          <Routes>
            <Route />
          </Routes>
            <Home projects={projects} modal={modal} setModal={setModal} setScrollTargetMarker={setScrollTargetMarker} scrollReady={scrollReady} />
        </main>
        <footer className="footer">
          <div className="footer__container-big">
            <div className="footer__main-info">
              <div className="footer-logo-wrapper">
                <Logo />
              </div>
              <p>
                Browse through my range of services that cater to various needs. From consultations to custom solutions, i have you covered.
              </p>
              <div className="copyright-block">
                <span className="copyright-block__copyright">©by benko 2023</span>
              </div>
            </div>

            <FooterBlock title={'navigation'} social={false}>
              {menuNav.map((item, i) => {
                return (
                  <MenuEl key={i} href={item.url} text={item.text} current={item.current === "true" ? true : false} />
                )
              })}
            </FooterBlock>

            <FooterBlock title={'Contacts'}>
              <div className="social-list">
                {menuIcons.map((item, i) => {
                  return (
                    <GsapMagnetic key={i}>
                      <a href={item.url} target="_blank" rel="noreferrer">
                        <img src={item.link} alt={item.alt} />
                      </a>
                    </GsapMagnetic>
                  )
                })}
              </div>

              <Button text="Write me" href='' />
            </FooterBlock>

            <div className="copyright-block copyright-block_mobile">
              <span className="copyright-block__copyright">©by benko 2023</span>
            </div>
          </div>

          <div className="footer__decor-wrapper decor-wrapper">
            <div className="decor-wrapper__wrapper">
              <div className="decor-wrapper__decor decor-wrapper__decor_one"></div>
              <div className="decor-wrapper__decor decor-wrapper__decor_two"></div>
              <div className="decor-wrapper__decor decor-wrapper__decor_three"></div>
              <div className="decor-wrapper__decor decor-wrapper__decor_four"></div>
            </div>
          </div>
        </footer>


        <div className={`overlay ${overlayActive ? 'active' : ''}`}></div>
      </div>

      <Preloader preloader={preloader} />
    </SmoothScroll>
  );
}

export default App;
