import React from "react"
import './index.scss';
import Logo from "../logo";

import { motion, useInView, useAnimation } from "framer-motion"

function Preloader({ preloader, finishStartPreloader }) {
  const preloaderText = React.useRef(null);

  const textInView = useInView(preloaderText, { once: true });
  const textInViewAnim = useAnimation();

  React.useEffect(() => {
    if (textInView) {
      textInViewAnim.start("visible");
    }
  }, [textInView, textInViewAnim]);

  return (
    <div className={`preloader ${preloader ? "active" : ""}`}>
      <div className="preloader__wrapper">
        <div className="preloader__decor-wrapper decor-wrapper">
          <div className="decor-wrapper__wrapper">
            <div className="decor-wrapper__decor decor-wrapper__decor_one"></div>
            <div className="decor-wrapper__decor decor-wrapper__decor_two"></div>
            <div className="decor-wrapper__decor decor-wrapper__decor_three"></div>
            <div className="decor-wrapper__decor decor-wrapper__decor_four"></div>
          </div>
        </div>

        {finishStartPreloader && <div>text</div>}
        {finishStartPreloader && 
            <>
            </>
        }
      </div>
    </div>
  );
}

export default Preloader;
