import React from "react";

import greenRing50 from "../img/decor/green-ring50-2.svg";
import orangeRing50 from "../img/decor/orange-ring50-1.svg";
import blueRing50 from "../img/decor/blue-ring50-1.svg";
import AsideBlock from "../components/aside-block";
import AsideMenuEl from "../components/aside-menu-el";
import { Parallax } from "react-scroll-parallax";
import { motion } from "framer-motion";

const social = [
  { title: "telegram", link: "https://t.me/benko_dev" },
  {
    title: "instagram",
    link: "https://instagram.com/benko_web_dev?igshid=OGQ5ZDc2ODk2ZA==",
  },
  { title: "dribbble", link: "https://dribbble.com/benko_web_dev" },
  {
    title: "linkedin",
    link: "https://www.linkedin.com/in/dima-dzyubenko-a7285420b/",
  },
];

const WriteMe = () => {
  const [nameValue, setNameValue] = React.useState("");
  const [emailValue, setEmailValue] = React.useState("");
  const [ideaValue, setIdeaValue] = React.useState("");
  const [serviceValue, setServiceValue] = React.useState("");
  const [messageValue, setMessageValue] = React.useState("");

  const BlueBrash = React.useRef(null);
  const OrangeBrash = React.useRef(null);

  const inputHandler = (e, seter) => {
    seter(e.target.value);
  };

  return (
    <section className="write-me">
      <div className="write-me__container-small">
        <div className="write-me__wrapper">
          <div className="write-me__top">
            <img
              src={blueRing50}
              alt=""
              className="write-me__decor write-me__decor-blue anim-circle-type1"
            />
            <img
              src={orangeRing50}
              alt=""
              className="write-me__decor write-me__decor-orange anim-circle-type2"
            />
            <img
              src={greenRing50}
              alt=""
              className="write-me__decor write-me__decor-green anim-circle-type3"
            />

            <Parallax speed={10}>
              <h1 className="write-me__title">
                let's turn your dream into reality
              </h1>
            </Parallax>
          </div>

          <div className="write-me__body">
            <form className="write-me__form write-form">
              <div className="write-form__inputs">
                <div
                  className="portfolio-brash-decor portfolio-brash-decor_blue"
                  ref={BlueBrash}>
                  <Parallax speed={2}>
                    <svg
                      viewBox="0 0 93 87"
                      fill="none"
                      xmlns="http://www.w3.org/2000/svg">
                      <motion.path
                        initial="hidden"
                        animate={portfolioBrashInViewAnim}
                        variants={{
                          hidden: {
                            opacity: 0,
                            pathLength: 0,
                          },
                          visible: {
                            opacity: 1,
                            pathLength: 1,
                            transition: {
                              duration: 1.2,
                              delay: 0.3,
                              ease: "easeIn",
                            },
                          },
                        }}
                        d="M74.2928 81.7265C80.6904 78.7666 94.7673 71.0552 84.081 63.6743C73.5648 56.4109 61.9528 57.0223 49.8897 57.5132C46.1579 57.665 40.391 58.1875 37.0804 55.7953C31.617 51.8474 38.7864 37.9059 40.0111 33.3034C46.0476 10.617 15.8173 15.9581 5.3208 5.45639"
                        stroke="#80C2FF"
                        strokeWidth="9"
                        strokeLinecap="round"
                      />
                    </svg>
                  </Parallax>
                </div>
                <div
                  className="portfolio-brash-decor portfolio-brash-decor_orange"
                  ref={portfolioOrangeBrash}>
                  <Parallax speed={2}>
                    <svg
                      viewBox="0 0 95 91"
                      fill="none"
                      xmlns="http://www.w3.org/2000/svg">
                      <motion.path
                        initial="hidden"
                        animate={portfolioBrashInViewSecondAnim}
                        variants={{
                          hidden: {
                            opacity: 0,
                            pathLength: 0,
                          },
                          visible: {
                            opacity: 1,
                            pathLength: 1,
                            transition: {
                              duration: 1.2,
                              delay: 0.3,
                              ease: "easeIn",
                            },
                          },
                        }}
                        d="M89.6852 56.5276C89.581 57.2131 89.6769 56.9519 88.9129 57.8181C85.5066 61.6805 81.7813 65.3303 78.1591 68.9818C72.6921 74.4931 66.801 81.5023 59.7894 85.1949C54.8968 87.7715 54.42 79.0907 53.9636 76.373C51.3353 60.7212 52.3129 44.5709 49.4267 28.9965C48.583 24.4438 41.5476 38.8671 41.4705 39.0178C40.9205 40.0935 36.8294 48.8911 35.1876 50.0297C29.5197 53.9605 23.0778 34.204 22.8311 33.6546C19.922 27.1779 17.2324 20.445 13.9975 14.1035C12.5761 11.3171 10.7143 7.50399 7.81409 5.7392C6.95842 5.21852 5.76402 5.17136 4.78719 5.02277"
                        stroke="#FFC122"
                        strokeWidth="9"
                        strokeLinecap="round"
                      />
                    </svg>
                  </Parallax>
                </div>

                <div className="write-form__field">
                  <input
                    id="name"
                    value={nameValue}
                    onChange={(e) => inputHandler(e, setNameValue)}
                    type="text"
                    className="write-form__input"
                  />

                  <label
                    htmlFor="name"
                    id="name-label"
                    className={`write-form__label ${
                      nameValue.length > 0 ? "active" : ""
                    }`}>
                    Your name
                  </label>
                </div>
                <div className="write-form__field">
                  <input
                    id="email"
                    type="email"
                    className="write-form__input"
                    value={emailValue}
                    onChange={(e) => inputHandler(e, setEmailValue)}
                  />

                  <label
                    htmlFor="email"
                    id="email-label"
                    className={`write-form__label ${
                      emailValue.length > 0 ? "active" : ""
                    }`}>
                    your email
                  </label>
                </div>
                <div className="write-form__field">
                  <input
                    id="idea"
                    type="text"
                    className="write-form__input"
                    value={ideaValue}
                    onChange={(e) => inputHandler(e, setIdeaValue)}
                  />

                  <label
                    htmlFor="idea"
                    id="idea-label"
                    className={`write-form__label ${
                      ideaValue.length > 0 ? "active" : ""
                    }`}>
                    describe your idea in few words
                  </label>
                </div>
                <div className="write-form__field">
                  <input
                    id="service"
                    type="text"
                    className="write-form__input"
                    value={serviceValue}
                    onChange={(e) => inputHandler(e, setServiceValue)}
                  />

                  <label
                    htmlFor="service"
                    id="service-label"
                    className={`write-form__label ${
                      serviceValue.length > 0 ? "active" : ""
                    }`}>
                    what service do you need
                  </label>
                </div>
                <div className="write-form__field">
                  <input
                    id="message"
                    type="text"
                    className="write-form__input"
                    value={messageValue}
                    onChange={(e) => inputHandler(e, setMessageValue)}
                  />

                  <label
                    htmlFor="message"
                    id="message-label"
                    className={`write-form__label ${
                      messageValue.length > 0 ? "active" : ""
                    }`}>
                    your message
                  </label>
                </div>
              </div>
            </form>
            <div className="write-aside">
              <div className="write-aside__child">
                <AsideBlock title="contacts">
                  <a href={`mailto:benkowebdev@gmail.com`}>
                    benkowebdev@gmail.com
                  </a>
                  <a href={`tel:+380634837597`}>+380634837597</a>
                </AsideBlock>
                <AsideBlock title="contacts">
                  {social.map((obj) => {
                    return (
                      <AsideMenuEl
                        key={obj.title}
                        text={obj.title}
                        link={obj.link}
                      />
                    );
                  })}
                </AsideBlock>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default WriteMe;
