import "./null.scss";
import "./ui.scss";
import "./App.scss";
import "./scheme.scss";
import React from "react";
import { Routes, Route } from "react-router-dom";
import Layout from "./components/Layout.jsx";
import menuItems from "./data/header-menu.json";

import Home from "./pages/Home";

import NotFound from "./pages/NotFound/index.jsx";

import Hounter from "./img/preview/hounter.png";
import CafeStreet from "./img/preview/CafeStreet.png";
import ArtNLife from "./img/preview/art&life.png";
import SashaShavit from "./img/preview/SashaShavit.png";
import Memcoin from "./img/preview/memcoin.png";
import ToDo from "./img/preview/todo.png";

const projectsPortfolio = [
  {
    title: "Hounter",
    label: "development",
    imageUrl: Hounter,
  },
  {
    title: "CafeStreet",
    label: "development",
    imageUrl: CafeStreet,
  },
  {
    title: "Art & Life",
    label: "development",
    imageUrl: ArtNLife,
  },
  {
    title: "Sasha Shavit",
    label: "development",
    imageUrl: SashaShavit,
  },
  {
    title: "Memcoin",
    label: "development",
    imageUrl: Memcoin,
  },
  {
    title: "ToDo App",
    label: "design & development",
    imageUrl: ToDo,
  },
];

export const PreloaderSeter = React.createContext(null);
export const PreloaderStat = React.createContext(null);

export const OverlayStat = React.createContext(null);

function App() {
  const [projects, setProjects] = React.useState([]);
  const [scrollReady, setScrollReady] = React.useState(false);
  const [modal, setModal] = React.useState({ active: false, index: 0 });
  const [scrollTargetMarker, setScrollTargetMarker] = React.useState(false);

  const [preloader, setPreloader] = React.useState(true);
  const [overlayActive, setOverlayActive] = React.useState(true);

  React.useEffect(() => {
    setProjects(projectsPortfolio.slice().reverse().slice(0, 4));
  }, []);

  return (
    <PreloaderSeter.Provider value={[setPreloader]}>
      <PreloaderStat.Provider value={[preloader]}>
        <OverlayStat></OverlayStat>
        <Routes>
          <Route
            path="/"
            element={
              <Layout
                scrollTargetMarker={scrollTargetMarker}
                setScrollTargetMarker={setScrollTargetMarker}
                scrollReady={scrollReady}
                setScrollReady={setScrollReady}
              />
            }>
            <Route
              index
              element={
                <Home
                  projects={projects}
                  modal={modal}
                  setModal={setModal}
                  setScrollTargetMarker={setScrollTargetMarker}
                  scrollReady={scrollReady}
                />
              }
            />
            <Route path="about" element={<div>About me</div>} />

            <Route path="*" element={<NotFound />} />
          </Route>
        </Routes>
      </PreloaderStat.Provider>
    </PreloaderSeter.Provider>
  );
}

export default App;
